import React, { Component } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import openSocket from "socket.io-client";
import {
  Progress,
  Container,
  Row
} from "reactstrap";


const io = openSocket('http://localhost:5000')
class App extends Component {

  state = {
    airQaulity: 0
  }

  componentDidMount = () => {
    setInterval(_ => io.emit('air_quality'), 100)
    io.on('air_quality_value', airQualityValue => this.setState({ airQaulity: airQualityValue / 10 }))

  }

  render() {
    return (
      <div className='m-5'>
        <div className='d-flex align-items-center justify-content-center mt-2'>
          <h1>Sensors dashboard</h1>
        </div>
        <Container style={{ marginTop: 50 }}>
          <Row className='d-flex align-items-center'>
            <h5>Air Pollution</h5>
            <Progress style={{ width: 300, marginLeft: 20, marginBottom: 5 }} col value={this.state.airQaulity.toString()} />
          </Row>
          <Row className='d-flex align-items-center'>
            <h5>Motion</h5>
            <div style={{ backgroundColor: this.state.motion ? 'green' : 'red' }}></div>
          </Row>
        </Container>

      </div>
    );
  }
}


export default App;
