import React, { Component } from 'react';
import {
    Progress,
    Container,
    Row,
    Input,
    InputGroup,
    InputGroupAddon,
    Button
} from "reactstrap";
import axios from 'axios';


class Dashboard extends Component {

    state = {
        sensors: [],
        showUrl: false,
        send_data: false,
        url: ''
    }

    componentDidMount = () => {
        this.getSensors()
    }

    getSensors = () => {
        axios.get('http://0.0.0.0:5000/sensors')
            .then(
                res => {
                    var sensors = []
                    for (var sensorKey in JSON.parse(res.data.sensors)) {
                        sensors.push(JSON.parse(res.data.sensors)[sensorKey])
                    }
                    this.setState({ sensors })
                    console.log(sensors)
                }
            )
    }

    render = () => {
        return (
            <div className='m-5'>
                <div className='d-flex align-items-center justify-content-center mt-2'>
                    <h1>Sensors dashboard</h1>
                </div>
                <Container style={{ marginTop: 50 }}>
                    {this.state.sensors.map(sensor => (
                        <Row className='d-flex align-items-center mt-5'>
                            <h5 style={{ width: 100 }}>{sensor.name}</h5>
                            {/* <Progress style={{ width: 300, marginBottom: 5 }} value={this.state.airQaulity.toString()} /> */}
                        </Row>
                    ))}


                    <Row className='mt-5'>
                        {this.state.showUrl ? (
                            <InputGroup>
                                <Input onChange={event => this.setState({ url: event.target.value })} value={this.state.url} placeholder="URL of destination" />
                                <InputGroupAddon addonType="append">
                                    {/* <Button
                                        onClick={_ => {
                                            this.setState(prevState => ({ send_data: !prevState.send_data }),
                                                _ => io.emit('url_info', { url: this.state.url, send_data: this.state.send_data })
                                            )
                                        }}
                                        style={{ backgroundColor: this.state.send_data ? 'red' : 'green' }}>{this.state.send_data ? 'STOP' : 'SEND'}</Button> */}
                                </InputGroupAddon>
                            </InputGroup>
                        ) : (
                                <p onClick={_ => this.setState({ showUrl: true })} style={{ color: 'blue', cursor: 'pointer' }}>send data</p>
                            )}
                    </Row>
                </Container>

            </div>

        )
    }
}

export default Dashboard;
