import React, { Component } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import openSocket from "socket.io-client";
import {
  Progress,
  Container,
  Row
} from "reactstrap";


const io = openSocket('http://localhost:5000')
var i = 0
class App extends Component {

  state = {
    airQaulity: 0,
    motion: false,
    sound: 0,
    dust: 0
  }

  componentDidMount = () => {
    setInterval(_ => {
      io.emit('sensors_data')
      i++;
    }, 100)
    // io.on('air_quality_value', airQualityValue => this.setState({  }))
    // io.on('motion_value', motionValue => this.setState({ }))
    io.on('data', sensorData => {
      if (i=== 10){
        i = 0
        // console.log(sensorData)
      }
      this.setState({ sound: (sensorData.sound > 50) ? (sensorData.sound / 2.5 - 50) : 10, airQaulity: sensorData.air_quality / 10, motion: sensorData.motion > 500, dust: sensorData.dust })
    })

  }

  render() {
    return (
      <div className='m-5'>
        <div className='d-flex align-items-center justify-content-center mt-2'>
          <h1>Sensors dashboard</h1>
        </div>
        <Container style={{ marginTop: 50 }}>
          <Row className='d-flex align-items-center mt-5'>
            <h5 style={{ width: 100 }}>Air Pollution</h5>
            <Progress style={{ width: 300, marginBottom: 5 }} value={this.state.airQaulity.toString()} />
          </Row>
          {/* <Row className='d-flex align-items-center mt-5'>
            <h5 style={{ width: 100 }}>PM Pollution</h5>
            <Progress style={{ width: 300, marginBottom: 5 }} value={this.state.dust.toString()} />
          </Row> */}
          <Row className='d-flex align-items-center mt-5'>
            <h5 style={{ width: 100 }}>Sound</h5>
            <Progress style={{ width: 300, marginBottom: 5 }} value={this.state.sound.toString()} />
          </Row>
          <Row className='d-flex align-items-center mt-5'>
            <h5 style={{ width: 100 }}>Motion</h5>
            <div style={{
              backgroundColor: this.state.motion ? 'green' : 'red',
              height: 50,
              width: 50,
              borderRadius: 50
            }}></div>
          </Row>
        </Container>

      </div>
    );
  }
}


export default App;
