import React, { Component } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import openSocket from "socket.io-client";
import {
  Progress,
  Container,
  Row
} from "reactstrap";


const io = openSocket('http://localhost:5000')
var i = 0
class App extends Component {

  state = {
    airQaulity: 0,
    motion: false,
    sound: 0
  }

  componentDidMount = () => {
    setInterval(_ => {
      io.emit('air_quality')
      io.emit('motion')
      io.emit('sound')
      i++;
      if (i === 10) {
        console.log(`air quality: ${this.state.airQaulity}`)
        console.log(`motion: ${this.state.motion}`)
        console.log(`sound: ${this.state.sound}`)
        i = 0
      }
    }, 100)
    io.on('air_quality_value', airQualityValue => this.setState({ airQaulity: airQualityValue / 10 }))
    io.on('motion_value', motionValue => this.setState({ motion: motionValue > 500 }))
    io.on('sound_value', soundValue => this.setState({ sound: soundValue }))

  }

  render() {
    return (
      <div className='m-5'>
        <div className='d-flex align-items-center justify-content-center mt-2'>
          <h1>Sensors dashboard</h1>
        </div>
        <Container style={{ marginTop: 50 }}>
          <Row className='d-flex align-items-center mt-5'>
            <h5 style={{ width: 100 }}>Air Pollution</h5>
            <Progress style={{ width: 300, marginBottom: 5 }} value={this.state.airQaulity.toString()} />
          </Row>
          <Row className='d-flex align-items-center mt-5'>
            <h5 style={{ width: 100 }}>Motion</h5>
            <div style={{
              backgroundColor: this.state.motion ? 'green' : 'red',
              height: 50,
              width: 50,
              borderRadius: 50
            }}></div>
          </Row>
        </Container>

      </div>
    );
  }
}


export default App;
