import React, { Component } from 'react';
import {
    Row,
    Col,
    InputGroup,
    Input,
    InputGroupAddon,
    PopoverBody,
    Popover,
    Button,
    Spinner
} from "reactstrap";
import axios from "axios";
import './Settings.css';
import { FaPlus } from "react-icons/fa";


class Settings extends Component {

    state = {
        sensors: [],
        showUrl: false,
        send_data: false,
        url: '',
        addSensorPopover: false,
        addActuatorPopover: false,
        addSensorLoading: true
    }

    componentDidMount = () => {
        this.getSensors()
    }

    getSensors = () => {
        axios.get('http://0.0.0.0:5000/sensors')
            .then(
                res => {
                    console.log(JSON.parse(res.data.sensors))
                }
            )
    }

    addSensor = () => {

    }

    render = () => {
        return (
            <div>
                <Row className="container">
                    <h4 className="label">Sensors: </h4>
                    <div className="sensor"></div>
                    <div className="sensor sensor__add" id="sensor-add">
                        <FaPlus />
                    </div>
                </Row>
                <Row className="container">
                    <h4 className="label">Actuators: </h4>
                    <div className="actuator"></div>
                    <div className="actuator actuator__add" id="actuator-add">
                        <FaPlus />
                    </div>
                </Row>

                <Row className='mt-5 ml-5'>
                    {this.state.showUrl ? (
                        <InputGroup>
                            <Input onChange={event => this.setState({ url: event.target.value })} value={this.state.url} placeholder="URL of destination" />
                            <InputGroupAddon addonType="append">
                                {/* <Button
                                        onClick={_ => {
                                            this.setState(prevState => ({ send_data: !prevState.send_data }),
                                                _ => io.emit('url_info', { url: this.state.url, send_data: this.state.send_data })
                                            )
                                        }}
                                        style={{ backgroundColor: this.state.send_data ? 'red' : 'green' }}>{this.state.send_data ? 'STOP' : 'SEND'}</Button> */}
                            </InputGroupAddon>
                        </InputGroup>
                    ) : (
                            <p onClick={_ => this.setState({ showUrl: true })} style={{ color: 'blue', cursor: 'pointer' }}>send data</p>
                        )}
                </Row>

                <Popover className="add-sensor-popover" isOpen={this.state.addSensorPopover} trigger='legacy' target="sensor-add" toggle={_ => this.setState(prevState => ({ addSensorPopover: !prevState.addSensorPopover }))}>

                    <PopoverBody className="add-sensor-form">
                        <h5>Add Sensor</h5>
                        <InputGroup className="add-sensor-form__input">
                            <InputGroupAddon addonType="prepend" >Xbee Id</InputGroupAddon>
                            <Input placeholder="Xbee Id" />
                        </InputGroup>
                        <InputGroup className="add-sensor-form__input">
                            <InputGroupAddon addonType="prepend" >Input Pin</InputGroupAddon>
                            <Input placeholder="Input Pin" />
                        </InputGroup>
                        <Button className="add-sensor-form__button">
                        <p className="add-sensor-form__error"></p>
                        {this.state.addSensorLoading ? (<Spinner size="sm"/>) : ('Add Sensor')}
                        </Button>

                    </PopoverBody>

                </Popover>

                <Popover className="add-actuator-popover" isOpen={this.state.addActuatorPopover} trigger='legacy' target="actuator-add" toggle={_ => this.setState(prevState => ({ addActuatorPopover: !prevState.addActuatorPopover }))}>
                    <PopoverBody>
                    </PopoverBody>
                </Popover>

            </div>
        )
    }
}

export default Settings
