import React, { Component } from 'react';
import {
    Row,
    Col
} from "reactstrap";
import axios from "axios";
import './Settings.css';
import { FaPlus } from "react-icons/fa";


class Settings extends Component {

    state = {
        sensor: []
    }

    componentDidMount = () => {
        this.getSensors()
    }

    getSensors = () => {
        axios.get('http://0.0.0.0:5000/sensors')
            .then(
                res => {
                    console.log(JSON.parse(res.data.sensors))
                }
            )
    }

    render = () => {
        return (
            <div>
                <Row className="container">
                    <h3>Sensors: </h3>
                    <div className="sensor"></div>
                    <div className="sensor sensor__add">
                        <FaPlus />
                    </div>
                </Row>
                <Row className="container">
                    <h3>Sensors: </h3>
                    <div className="actuator"></div>
                    <div className="actuator actuator__add">
                        <FaPlus />
                    </div>
                </Row>
            </div>
        )
    }
}

export default Settings
